import React, { useEffect, useState } from "react";
import Nav from "./nav";
import Login from "./login";
import Crime from "./displayData";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import "./App.css";

function App() {
  return (
    <Router>
      <div className="App">
        <Nav />
        <Switch>
          <Route path="/" exact component={Home} />
          <Route path="/login" component={Login} />
        </Switch>
      </div>
    </Router>
  );
}

function Home() {
  const MAP_BOX_API =
    "pk.eyJ1IjoiMnBoZWxqNTYiLCJhIjoiY2trc2NyaGExMGMxeDJ3cGw3NjBqNng1NyJ9.ZJp17pLpWHvxtYSVLxARNg";

  // state which holds empty string - locations
  const [inputLocations, setInputLocation] = useState({
    street: "",
    city: "",
  });

  // state which sends user input to empty string and runs useEffect
  const [query, setQuery] = useState("38 Tenterton Avenue Southampton");

  const [crimeData, setCrimeData] = useState({});

  useEffect(() => {
    getData();
  }, [query]);

  const getData = async () => {
    // fetch location
    const locationResponse = await fetch(
      `https://api.mapbox.com/geocoding/v5/mapbox.places/${query}%20UK.json?limit=1&access_token=${MAP_BOX_API}`
    );
    const locationData = await locationResponse.json();
    const location = locationData.features[0].center;

    // fetch crime data
    const crimeResponse = await fetch(
      `https://data.police.uk/api/crimes-at-location?date=2019-10&lat=${location[1]}&lng=${location[0]}`
    );
    const crimeData = await crimeResponse.json();

    setCrimeData(
      crimeData.reduce((allCrimes, crime) => {
        if (crime.category in allCrimes) {
          allCrimes[crime.category]++;
        } else {
          allCrimes[crime.category] = 1;
        }
        return allCrimes;
      }, {})
    );
  };

  function streetName(e) {
    // send data to string state
    setInputLocation({ ...inputLocations, [e.target.name]: e.target.value });
  }

  function submitForm(e) {
    e.preventDefault();
    setQuery(Object.values(inputLocations).join(" "));
  }

  return (
    <div className="pages">
      <div className="home-container">
        <h1>The crime data app to help you feel safe.</h1>
        <form onSubmit={submitForm}>
          <input
            type="text"
            placeholder="Street Name"
            name="street"
            onChange={streetName}
          />
          <input
            type="text"
            placeholder="City / Town"
            name="city"
            onChange={streetName}
          />
          <button className="">Find Crime Data</button>
        </form>
        <p className="form-info">
          Search by town, city or simply click the location icon to get
          information on crime in your area. Data is collected from
          ‘data.police.uk’.
        </p>
      </div>
      {crimeData.map((recipe) => (
        <Crime />
      ))}
    </div>
  );
}

export default App;
