import React, { useEffect, useState } from "react";
import Nav from "./nav";
import Login from "./login";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import "./App.css";

function App() {
  // fetch crime data
  // fetch mapBox longitude / latitude
  const MAP_BOX_API =
    "pk.eyJ1IjoiMnBoZWxqNTYiLCJhIjoiY2trc2NyaGExMGMxeDJ3cGw3NjBqNng1NyJ9.ZJp17pLpWHvxtYSVLxARNg";

  useEffect(() => {
    getData();
  }, []);

  const getData = async () => {
    const lResponse = await fetch(
      `https://api.mapbox.com/geocoding/v5/mapbox.places/38%20Tenterton%20Avenue%20Southampton%20UK.json?limit=1&access_token=${MAP_BOX_API}`
    );
    const lData = await lResponse.json();
    const location = lData.features[0].center;

    const cResponse = await fetch(
      `https://data.police.uk/api/crimes-at-location?date=2019-10&lat=${location[1]}&lng=${location[0]}`
    );
    const cData = await cResponse.json();
    console.log(cData);
  };

  return (
    <Router>
      <div className="App">
        <Nav />
        <Switch>
          <Route path="/" exact component={Home} />
          <Route path="/login" component={Login} />
        </Switch>
      </div>
    </Router>
  );
}

function Home() {
  return (
    <div className="pages">
      <div className="home-container">
        <h1>The crime data app to help you feel safe.</h1>
        <form>
          <input type="text" placeholder="Street Name" />
          <input type="text" placeholder="City / Town" />
          <button className="">Find Crime Data</button>
        </form>
        <p className="form-info">
          Search by town, city or simply click the location icon to get
          information on crime in your area. Data is collected from
          ‘data.police.uk’.
        </p>
      </div>
    </div>
  );
}

export default App;
