import React, { useEffect, useState } from "react";
import Nav from "./nav";
import Login from "./login";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import "./App.css";

function App() {
  return (
    <Router>
      <div className="App">
        <Nav />
        <Switch>
          <Route path="/" exact component={Home} />
          <Route path="/login" component={Login} />
        </Switch>
      </div>
    </Router>
  );
}

function Home() {
  const MAP_BOX_API =
    "pk.eyJ1IjoiMnBoZWxqNTYiLCJhIjoiY2trc2NyaGExMGMxeDJ3cGw3NjBqNng1NyJ9.ZJp17pLpWHvxtYSVLxARNg";

  // state which holds empty string
  const [inputLocation, useInputLocation] = useState("");
  // state which sends user input to empty string and runs useEffect
  // state which holds data

  // function to hold user input,
  // onClick, sends above function data to sendData state.

  useEffect(() => {
    getData();
  }, []);

  const getData = async () => {
    // fetch location
    const locationResponse = await fetch(
      `https://api.mapbox.com/geocoding/v5/mapbox.places/38%20Tenterton%20Avenue%20Southampton%20UK.json?limit=1&access_token=${MAP_BOX_API}`
    );
    const locationData = await locationResponse.json();
    const location = locationData.features[0].center;

    // fetch crime data
    const crimeResponse = await fetch(
      `https://data.police.uk/api/crimes-at-location?date=2019-10&lat=${location[1]}&lng=${location[0]}`
    );
    const crimeData = await crimeResponse.json();
  };

  function streetName(e) {
    // send data to string state
    useInputLocation(e.target.value);
  }

  return (
    <div className="pages">
      <div className="home-container">
        <h1>The crime data app to help you feel safe.</h1>
        <form>
          <input type="text" placeholder="Street Name" onChange={streetName} />
          <input type="text" placeholder="City / Town" onChange={streetName} />
          <button className="">Find Crime Data</button>
        </form>
        <p className="form-info">
          Search by town, city or simply click the location icon to get
          information on crime in your area. Data is collected from
          ‘data.police.uk’.
        </p>
      </div>
    </div>
  );
}

export default App;
